  CREATE OR REPLACE NONEDITIONABLE TRIGGER "HWAHYE"."STOCK_STATUS" 
BEFORE UPDATE OF PRODUCT_STOCK ON PRODUCT
FOR EACH ROW
BEGIN
    IF :NEW.PRODUCT_STOCK = 0 AND :OLD.PRODUCT_STOCK > 0
    THEN
        :NEW.PRODUCT_STATE := 'N';
    END IF;

    IF :NEW.PRODUCT_STOCK > 0 AND :OLD.PRODUCT_STOCK = 0
    THEN
        :NEW.PRODUCT_STATE := 'Y';
    END IF;
END;

/
