mapper.select:
select *
from product
where user_no = #{userNo}

	and product_no = #{productNo}

order by product_no desc

mapper.update:
update product
set product_stock = #{productStock}
where product_no = #{productNo} and user_no = #{userNo}

update coupon
set coupon_discount = #{couponDiscount}, coupon_status = #{couponStatus}
where coupon_no = #{couponNo}

mapper.select:
select *
from attachment
where attm_level = 0 and attm_position = 0 and position_no = #{productNo}

mapper.update:
update product
set product_name = #{productName}, product_detail = #{productDetail}, product_stock = #{productStock}, product_price = #{productPrice}
where product_no = #{productNo}

mapper.select:
select *
from answer
where question_no = #{questionNo}

select *
from question
where question_no = #{questionNo}

mapper.update:
update answer
set answer_content = #{answerContent}
where answer_no = #{answerNo}

mapper.select:
select *
from experience_group
where user_no = #{userNo}

mapper.update:
update review_answer
set review_answer_content = #{reviewAnswerContent}
where review_answer_id = #{reviewAnswerId}

update review
set review_status = 'N'
where review_no = #{reviewNo}

mapper.select:
select *
from board
where board_id = #{boardId}

select *
from admin_reply
where board_id = #{boardId}

mapper.update:
update board
set board_content = #{boardContent}
where board_id = #{boardId}

update board
set board_status = 'N'
where board_id = #{boardId}
